mapper.select:
SELECT d.dept_id, d.dept_name, d.parent_id, d.tenant_id, d.create_time, d.update_time, d.create_by, d.update_by, t.tenant_name, ( SELECT dept_name FROM cpms_system_dept WHERE dept_id = d.parent_id ) AS superiorDept FROM cpms_system_dept d INNER JOIN cpms_system_tenant t ON t.tenant_id = d.tenant_id
d.del_flag = 0
and d.dept_name like CONCAT('%',#{deptName},'%' )
and d.tenant_id = #{tenantId}
and t.tenant_name like CONCAT('%',#{tenantName},'%' )
limit #{startIndex},#{pageSize}
