mapper.select:
SELECT r.role_name,r.role_id,r.role_code,role_desc,ru.user_id
FROM  cpms_system_role r,cpms_system_role_user ru
WHERE ru.role_id= r.role_id AND r.del_flag=0 AND ru.user_id = #{userId};

SELECT count(1) FROM cpms_system_role r LEFT JOIN cpms_system_dept d ON d.dept_id = r.dept_id
r.del_flag = 0
and r.dept_id = #{deptId}
and r.tenant_id = #{tenantId}

SELECT r.role_name, r.role_id, r.role_code, r.role_desc, r.dept_id, r.create_time, r.update_time, r.create_by, r.update_by, d.dept_name FROM cpms_system_role r LEFT JOIN cpms_system_dept d ON d.dept_id = r.dept_id
r.del_flag = 0
and r.dept_id = #{deptId}
and r.tenant_id = #{tenantId}
limit #{startIndex},#{pageSize}
