mapper.select:
SELECT
count(1)
FROM  cpms_system_tenant t

    t.del_flag = 0

        and t.tenant_name like CONCAT('%',#{tenantName},'%' )


        and t.tenant_status = #{tenantStatus}


        and t.lease_time_start >= #{leaseTimeStart}


        and t.lease_time_end <= #{leaseTimeEnd}

SELECT tenant_id, tenant_name, contacts, contact_number, address, lease_time_start, lease_time_end, tenant_status, create_time, update_time, create_by, update_by FROM cpms_system_tenant t
t.del_flag = 0
and t.tenant_name like CONCAT('%',#{tenantName},'%' )
and t.tenant_status = #{tenantStatus}
and t.lease_time_start >= #{leaseTimeStart}
and t.lease_time_end <= #{leaseTimeEnd}
limit #{startIndex},#{pageSize}
