mapper.select:
select count(1)
from
cpms_system_user u
LEFT JOIN cpms_system_dept d ON d.dept_id = u.dept_id

    and u.del_flag = 0

        and u.tenant_id = #{tenantId}


        and u.user_id = #{userId}


        and u.user_account = #{userAccount}


        and u.user_mobile = #{userMobile}


        and u.user_sex = #{userSex}


        and u.dept_id = #{deptId}


        and u.post_id = #{postId}

select u.user_id, u.user_account, u.user_name, u.user_login_ip, u.last_login_time, u.user_avatar, u.user_sex, u.user_mobile, u.user_status, u.create_time, u.update_time, u.create_by, u.update_by, d.dept_name, p.post_name, p.post_id from cpms_system_user u LEFT JOIN cpms_system_dept d ON d.dept_id = u.dept_id LEFT JOIN cpms_system_post p ON u.post_id = p.post_id
and u.del_flag = 0
and u.tenant_id = #{tenantId}
and u.user_id = #{userId}
and u.user_account = #{userAccount}
and u.user_mobile = #{userMobile}
and u.user_sex = #{userSex}
and u.dept_id = #{deptId}
and u.post_id = #{postId}
limit #{startIndex},#{pageSize}
